import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Shop() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("http://localhost:8009/api/productList");
        setProducts(response.data.data); // Assuming response.data.data contains the array of products
      } catch (error) {
        console.log('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const handleReadMore = (productId) => {
    // Toggle the expanded state of the product
    const updatedProducts = products.map(product => {
      if (product._id === productId) {
        return { ...product, expanded: !product.expanded };
      }
      return product;
    });
    setProducts(updatedProducts);
  };

  return (
    <section className="shop_section layout_padding">
      <div className="container">
        <div className="heading_container heading_center">
          <h2>Latest Products</h2>
        </div>
        <div className="row">
          {products.map((product) => (
            <div className="col-sm-6 col-md-4 col-lg-3" key={product._id}>
              <div className="box">
                <a href="#">
                  <div className="img-box">
                    <img src={product.url} alt={product.title} />
                  </div>
                  <div className="detail-box">
                    <h6>{product.title}</h6>
                    <h6>Price: <span>${product.price}</span></h6>
                    {product.expanded ? (
                      <>
                        <p>{product.description}</p>
                        <p>Category: {product.category}</p>
                        <button onClick={() => handleReadMore(product._id)}>Read Less</button>
                      </>
                    ) : (
                      <button onClick={() => handleReadMore(product._id)}>Read More</button>
                    )}
                  </div>
                  <div className="new">
                    <span>New</span>
                  </div>
                </a>
              </div>
            </div>
          ))}
        </div>
        <div className="btn-box">
          <a href="#">View All Products</a>
        </div>
      </div>
    </section>
  );
}

export default Shop;
