import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AddToCart.css'; // Import the CSS file

function AddToCart() {
  const [cart, setCart] = useState([]);
  const [error, setError] = useState("");
  const [totalPrice, setTotalPrice] = useState(0); // State for total price
  const userId = sessionStorage.getItem("userId");

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (!userId) {
          setError("Please login first");
          return;
        }

        const data = { userId };
        const response = await axios.post("http://localhost:8009/api/cartList", data);

        console.log("Response from server:", response.data); // Log response from server for debugging

        if (response.data.status) {
          setCart(response.data.result); // Update cart state with fetched data
          setError(""); // Clear error if data is successfully fetched
        } else {
          setError("Failed to fetch cart data from server.");
        }
      } catch (err) {
        console.error("Error fetching cart data:", err);
        setError("Failed to fetch cart data. Please try again later.");
      }
    };

    fetchData();
  }, [userId]);

  // Function to calculate total price
  const calculateTotalPrice = () => {
    let total = 0;
    cart.forEach(item => {
      total += item.productId.price * item.quantity +5;
    });
    setTotalPrice(total);
  };

  useEffect(() => {
    calculateTotalPrice(); // Calculate total price whenever cart changes
  }, [cart]);

  const updateCartItem = async (action, index) => {
    try {
      const updatedCart = [...cart];
      let newQuantity;

      if (action === 'increment') {
        newQuantity = updatedCart[index].quantity + 1;
      } else if (action === 'decrement') {
        newQuantity = updatedCart[index].quantity - 1;
      }

      // If the quantity is 0 or less, remove the item from the cart
      if (newQuantity <= 0) {
        await axios.post("http://localhost:8009/api/updateCart", {
          userId,
          productId: updatedCart[index].productId._id,
          quantity: newQuantity
        });
        updatedCart.splice(index, 1);
      } else {
        await axios.post("http://localhost:8009/api/updateCart", {
          userId,
          productId: updatedCart[index].productId._id,
          quantity: newQuantity
        });
        updatedCart[index].quantity = newQuantity;
      }

      setCart(updatedCart);
    } catch (error) {
      console.error("Error updating cart item:", error);
    }
  };

  console.log("Current cart:", cart); // Log current cart state for debugging

  return (
    <div className="cart-wrapper">
      <h1 className="cart-title">Shopping Cart</h1>
      {error && <p className="error">{error}</p>}
      <div className="cart-container">
        <ul>
          {cart.map((item, index) => (
            <li key={`${item.productId._id}-${index}`} className="cart-item">
              <img src={item.productId.url} alt={item.productId.title} className="cart-item-image" />
              <div className="cart-item-details">
                <h2>{item.productId.title}</h2>
                <p>Quantity:
                  <button className="quantity-btn" onClick={() => updateCartItem('decrement', index)}>-</button>
                  {item.quantity}
                  <button className="quantity-btn" onClick={() => updateCartItem('increment', index)}>+</button>
                </p>
              </div>
            </li>
          ))}
        </ul>
        <p>Total Price: ${totalPrice}</p> {/* Display total price */}
        <button> Buy Now</button>
      </div>
    </div>
  );
}

export default AddToCart;
