import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Shop.css'; // Ensure this path matches your project structure

function Shop() {
  const [products, setProducts] = useState([]);
  const [popup, setPopup] = useState({ visible: false, message: '' });
  
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.post("http://localhost:8009/api/productList");
        setProducts(response.data.data); // Assuming response.data.data contains the array of products
      } catch (error) {
        console.log('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const userId = sessionStorage.getItem("userId");

  const addToCart = async (item) => {
    if (!userId) {
      setPopup({ visible: true, message: 'You have to login first' });
      setTimeout(() => {
        setPopup({ visible: false, message: '' });
      }, 3000); // Hide popup after 3 seconds
      return;
    }

    const data = {
      userId: userId,
      productId: item._id,
      quantity: 1 // Initial quantity
    };

    try {
      const response = await axios.post("http://localhost:8009/api/addCartList", data);
      console.log(response.data);
      // Optionally update the cart state or show a success message
    } catch (error) {
      console.log('Error adding to cart:', error);
    }
  };

  return (
    <>
      <section className="shop_section layout_padding">
        <div className="container">
          <div className="heading_container heading_center">
            <h2>Latest Products</h2>
          </div>
          <div className="row">
            {products.map((product) => (
              <div className="col-sm-6 col-md-4 col-lg-6" key={product._id}>
                <div className="box">
                  <a href="#">
                    <div className="img-box">
                      <img src={product.url} alt={product.title} />
                    </div>
                    <div className="detail-box">
                      <h6>{product.title}</h6>
                      <p>{product.description}</p>
                      <h6>
                        Price <span>${product.price}</span>
                      </h6>
                      <p>Category: {product.category}</p>
                      <button onClick={() => addToCart(product)}>Add to Cart</button>
                    </div>
                    <div className="new">
                      <span>New</span>
                    </div>
                  </a>
                </div>
              </div>
            ))}
          </div>
          <div className="btn-box">
            <a href="#">View All Products</a>
          </div>
        </div>
      </section>
      {popup.visible && (
        <div className="popup">
          <p>{popup.message}</p>
        </div>
      )}
    </>
  );
}

export default Shop;
