import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AddToCart.css'; // Import the CSS file

function AddToCart() {
  const [cart, setCart] = useState([]);
  const [error, setError] = useState("");
  const userId = sessionStorage.getItem("userId");

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (!userId) {
          setError("Please login first");
          return;
        }

        const data = { userId };
        const response = await axios.post("http://localhost:8009/api/cartList", data);

        console.log("Response from server:", response.data); // Log response from server for debugging

        if (response.data.status) {
          setCart(response.data.result); // Update cart state with fetched data
          setError(""); // Clear error if data is successfully fetched
        } else {
          setError("Failed to fetch cart data from server.");
        }
      } catch (err) {
        console.error("Error fetching cart data:", err);
        setError("Failed to fetch cart data. Please try again later.");
      }
    };

    fetchData();
  }, [userId]);

  const incrementQuantity = async (index) => {
    const updatedCart = [...cart];
    updatedCart[index].quantity++; // Increase quantity locally

    // Update quantity on the server
    const productId = updatedCart[index].productId;
    const quantity = updatedCart[index].quantity;
    updateCartItem(productId, quantity);

    setCart(updatedCart); // Update local state
  };

  const decrementQuantity = async (index) => {
    const updatedCart = [...cart];
    if (updatedCart[index].quantity > 1) {
      updatedCart[index].quantity--; // Decrease quantity locally

      // Update quantity on the server
      const productId = updatedCart[index].productId;
      const quantity = updatedCart[index].quantity;
      updateCartItem(productId, quantity);

      setCart(updatedCart); // Update local state
    } else {
      // Remove item from cart if quantity becomes 0
      updatedCart.splice(index, 1);
      setCart(updatedCart);

      // Remove item from server-side cart
      const productId = cart[index].productId;
      const quantity = 0; // Set quantity to 0 to indicate removal
      updateCartItem(productId, quantity);
    }
  };

  const updateCartItem = async (productId, quantity) => {
    try {
      const data = {
        userId: userId,
        productId: productId,
        quantity: quantity
      };

      const response = await axios.post("http://localhost:8009/api/addCartList", data);
      console.log("Cart updated Successfully", response.data);
    } catch (error) {
      console.log(error);
    }
  };

  console.log("Current cart:", cart); // Log current cart state for debugging

  return (
    <div className="cart-wrapper">
      <h1 className="cart-title">Shopping Cart</h1>
      {error && <p className="error">{error}</p>}
      <div className="cart-container">
        <ul>
          {cart.map((item, index) => (
            <li key={`${item.productId._id}-${index}`} className="cart-item">
              <img src={item.productId.url} alt={item.productId.title} className="cart-item-image" />
              <div className="cart-item-details">
                <h2>{item.productId.title}</h2>
                <p>
                  Quantity:
                  <button className="quantity-btn" onClick={() => decrementQuantity(index)}>-</button>
                  {item.quantity}
                  <button className="quantity-btn" onClick={() => incrementQuantity(index)}>+</button>
                </p>
              </div>
            </li>
          ))}
        </ul>
        <button> Buy Now</button>
      </div>
    </div>
  );
}

export default AddToCart;
