import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Signup.scss'
function Signup() {
  const [loginClicked, setLoginClicked] = useState(false);
  const [signupClicked, setSignupClicked] = useState(false);

  const handleLoginClick = () => {
    setLoginClicked(true);
    setSignupClicked(false);
  };

  const handleSignupClick = () => {
    setLoginClicked(false);
    setSignupClicked(true);
  };

  return (
    <>
      <title>Signup Form</title>
      <div className={`body ${loginClicked ? 'login-bg' : signupClicked ? 'signup-bg' : ''}`}>
        {/* FORMULAIRE DE CONNEXION */}
        <div className="card">
          <h2>Signup Form</h2>
          {/* SE CONNECTER / S'INSCRIRE */}
          <div className="login_register">
            <Link
              to="/login"
              className={`login ${loginClicked ? 'active' : ''}`}
              onClick={handleLoginClick}
              target="_blank"
            >
              Login
            </Link>
            <Link
              to="/"
              className={`register ${signupClicked ? 'active' : ''}`}
              onClick={handleSignupClick}
              target="_blank"
            >
              Signup
            </Link>
          </div>
          {/* FORMULAIRE */}
          <form className="form">
            <input type="username" placeholder="UserName" className="username" />
            <input type="email" placeholder="Email Adress" className="email" />
            <input type="password" placeholder="password" className="pass" />
            <input type="password" placeholder="Confirm Password" className="confirm_pass" />
          </form>
          {/* MOT DE PASSE OUBLIE ? */}
          <Link to="#" className="fp">
            Forgot password?
          </Link>
          {/* BOUTTON LOGIN */}
          <button type="submit" className="login_btn">
            Signup
          </button>
        </div>
      </div>
    </>
  );
}

export default Signup;
